pragma solidity ^0.8.22;

import {ISettlementContract, CrossChainOrder, ResolvedCrossChainOrder} from "./interfaces/IERC7683.sol";
import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import {MessageHashUtils} from "@openzeppelin/contracts/utils/cryptography/MessageHashUtils.sol";
import {Context} from "@openzeppelin/contracts/utils/Context.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";

contract SettlementContract is
    ISettlementContract,
    Context,
    Ownable
{
    using ECDSA for bytes32;
    using MessageHashUtils for bytes32;

    ///
    /// Constructor
    ///

    constructor() Ownable(_msgSender()) {}

    ///
    /// Order Hashing
    ///

    function computeHash(CrossChainOrder calldata order) external pure returns (bytes32) {
        return _hashOrder(order);
    }

    function _hashOrder(CrossChainOrder memory order) internal pure returns (bytes32) {
        return keccak256(abi.encode(order));
    }

    function _verifySignature(CrossChainOrder memory order, bytes memory signature) internal view returns (bool) {
        return _hashOrder(order)
            .toEthSignedMessageHash()
            .recover(signature) == _msgSender();
    }

    ///
    /// Whitelisted Settlement Contracts
    ///

    mapping(address => bool) internal _isSettlementSupported;
    mapping(address => uint) internal _settlementToChainId;

    function whitelistSettlementContract(address settlementContract, uint chainId) external onlyOwner {
        _isSettlementSupported[settlementContract] = true;
        _settlementToChainId[settlementContract] = chainId;
    }

    ///
    /// Order Registration
    ///

    mapping(bytes32 => CrossChainOrder) internal _orders;
    mapping(address => uint256) internal _nonceOf;

    function initiate(CrossChainOrder calldata order, bytes calldata signature, bytes calldata fillerData) external {

    }

    function nonceOf(address user) external returns (uint256) {
        return _nonceOf[user];
    }

    ///
    /// Order Resolving
    ///

    function resolve(CrossChainOrder calldata order, bytes calldata fillerData) external view returns (ResolvedCrossChainOrder memory) {

    }
